sys_script_include.script:
//ATTENZIONE, FUNZIONE IN BASE ALLA DATA DI OGGI +30 GIORNI, QUINDI SE DEVO USARLA TANTE VOLTE DEVO FARE REFERENZA A QUESTA DATA +30

var ClaseDataScript = Class.create();
ClaseDataScript.prototype = {
    initialize: function() {
    },
	isDatefuture: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		rightnow.addDaysLocalTime(30);
		var testdate = new GlideDateTime(strDate);

		// If the testdate is before rightnow, return true, else return false
		if (testdate.after(rightnow)) {
			return true;
		}
		else {
			return false;
		}

	},

    type: 'ClaseDataScript'
};
