sys_script_include.script:
var getCognome = Class.create();
// Extend the global.AbstractAjaxProcessor class
getCognome.prototype = Object.extendsObject(global.AbstractAjaxProcessor,{
    // Define the getEmail function.  
    // Create a GlideRecord for the User table.
    // Use the sysparm_userID passed from the client side to retrieve a record from the User table.
    // Return the email address for the requested record
    getcognomenow: function() {
        var userRecord = new GlideRecord("x_570202_bacheca_utente");
        userRecord.get(this.getParameter('sysparm_number'));
        return userRecord.u_string_1 + '';
    },
    getemailnow: function() {
        var userRecord = new GlideRecord("x_570202_bacheca_utente");
        userRecord.get(this.getParameter('sysparm_number'));
        return userRecord.email + '';
    },
	getEmails: function() {

var annuncioRecord = new GlideRecord('x_570202_bacheca_annuncio');
annuncioRecord.get(this.getParameter('sysparm_number'));

var utenteID = annuncioRecord.emailannuncio +'';
var utenteRecord= new GlideRecord('x_570202_bacheca_utente');

utenteRecord.get(utenteID);

return utenteRecord.email + '';

},


	type: 'getCognome'
});
